import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { getUsers, addExercise } from "./../../_actions/index";

export const CreateExercise = () => {
  const dispatch = useDispatch();
  const users = useSelector((state) => state.users.users);
  // const user1 = useSelector((state) => state.exercises.username);

  useEffect(() => {
    dispatch(getUsers());
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    setExercise({ ...exercise, username: users[0] });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [users]);

  const [exercise, setExercise] = useState({
    username: "",
    description: "",
    duration: null,
    date: new Date(),
  });

  const { username, description, duration, date } = exercise;
  console.log("exercise:", exercise);

  const onSubmit = (e) => {
    e.preventDefault();

    const exercise_record = {
      username,
      description: description.charAt(0).toUpperCase() + description.slice(1),
      duration,
      date,
    };
    console.log("this", exercise_record);
    dispatch(addExercise(exercise_record));
    window.location = "/";
  };

  return (
    <div>
      <h3>Create New Exercise Log</h3>
      <form onSubmit={onSubmit}>
        <div className='form-group'>
          <label>Username: </label>
          <select
            required
            className='form-control'
            value={username}
            onChange={(e) =>
              setExercise({ ...exercise, username: e.target.value })
            }>
            {users.map(function (user) {
              return (
                <option key={user} value={user}>
                  {user}
                </option>
              );
            })}
          </select>
        </div>
        <div className='form-group'>
          <label>Description: </label>
          <input
            type='text'
            required
            className='form-control'
            value={description}
            onChange={(e) =>
              setExercise({ ...exercise, description: e.target.value })
            }
          />
        </div>
        <div className='form-group'>
          <label>Duration (in minutes): </label>
          <input
            type='text'
            required
            className='form-control'
            value={duration}
            onChange={(e) =>
              setExercise({ ...exercise, duration: e.target.value })
            }
          />
        </div>
        <div className='form-group'>
          <label>Date: </label>
          <div>
            <DatePicker
              selected={date}
              onChange={(e) => setExercise({ ...exercise, date: e })}
            />
          </div>
        </div>

        <div className='form-group'>
          <input
            type='submit'
            value='Create Exercise Log'
            className='btn btn-primary'
          />
        </div>
      </form>
    </div>
  );
};
