import {
  FETCH_EXERCISES,
  FETCH_EXERCISES_ERROR,
  FETCH_USER,
  LOADING,
} from "./../_actions/types";

const initialState = {
  exercises: [],
  username: "",
  loading: false,
  error: "",
};

export default (state = initialState, action) => {
  switch (action.type) {
    case LOADING:
      return { ...state, loading: true };
    case FETCH_EXERCISES:
      return {
        ...state,
        exercises: action.payload,
        error: "",
        loading: false,
      };
    case FETCH_USER:
      return {
        ...state,
        username: action.payload,
      };
    case FETCH_EXERCISES_ERROR:
      return { ...state, error: action.payload, loading: false };
    // case FETCH_NAME:
    //   return { ...state, city: action.payload };
    // case FETCH_DETAILS:
    //   return { ...state, current: action.payload, loading: false, error: "" };
    default:
      return state;
  }
};
